INSERT INTO `wp_huge_itportfolio_images` (`id`, `name`, `portfolio_id`, `description`, `image_url`, `sl_url`, `sl_type`, `link_target`, `ordering`, `published`, `published_in_sl_width`, `category`) VALUES
(1, 'Nitrogen', '1', '<b>Nitrogen</b>\r\n<p>\r\nNitrogen essentially promotes fast plant growth. However, a forced, fast-growing plant tends to be weak overall and is more susceptible to insect damage and disease. Always use a nitrogen-rich fertilizer after the plant has been established. It is best to use a slow-release fertilizer when attempting to increase the nitrogen content. Nitrogen is released periodically, giving your plants a light boost instead of a shot. Organic fertilizers are naturally slow releasing.</p>', 'http://greengujaratnursery.glocalinfotech.in/wp-content/uploads/2015/03/image0.jpg;', '', 'image', 'on', 0, 1, NULL, 'My First Category,My Third Category,'),
(9, 'Desina', '2', 'loren lispomloren lispomloren lispomloren lispomloren lispomloren lispomloren lispom', 'http://greengujaratnursery.glocalinfotech.in/wp-content/uploads/2015/03/best-indoor-plants-1.gif.png;', '', '', 'on', 0, 2, 1, ''),
(10, 'Nitrogen', '3', 'Nitrogen\r\n\r\nNitrogen essentially promotes fast plant growth. However, a forced, fast-growing plant tends to be weak overall and is more susceptible to insect damage and disease. Always use a nitrogen-rich fertilizer after the plant has been established. It is best to use a slow-release fertilizer when attempting to increase the nitrogen content. Nitrogen is released periodically, giving your plants a light boost instead of a shot. Organic fertilizers are naturally slow releasing.', 'http://greengujaratnursery.glocalinfotech.in/wp-content/uploads/2015/03/image0.jpg;', '', '', 'on', 0, 2, 1, ''),
(2, 'Phosphorus', '1', '<b>Phosphorus</b>\r\n<p>\r\nPhosphorus encourages strong root growth, prolific blooming and fruit production. A fertilizer high in phosphorus should be used to amend the soil before planting and used throughout the plant''s infancy. Phosphorus is encouraged for vegetable gardens and flowering plants, such as fruit tree and roses. Organic fertilizers typically use bone meal to boost the phosphorus content.</p>', 'http://greengujaratnursery.glocalinfotech.in/wp-content/uploads/2015/03/Phosphorus_Fertilizer.jpg;', '', 'image', 'on', 1, 1, NULL, 'My Second Category,'),
(3, 'Potassium', '1', '<b>Potassium</b>\r\n\r\n<p>Potassium, or potash, boosts your plant''s hardiness to disease and insect damage. It strengthens the overall plant and is best used before and during the winter months. Winterizer fertilizers are typically high in potassium. However, always use fertilizer high in potassium with extreme caution and care. These fertilizers have a high salt index, according to Donnan.com, and over-application can burn or kill the plant.</p>', 'http://greengujaratnursery.glocalinfotech.in/wp-content/uploads/2015/03/npk-fertilizer.jpg;http://greengujaratnursery.glocalinfotech.in/wp-content/plugins/portfolio-gallery/Front_images/projects/3.1.jpg;http://greengujaratnursery.glocalinfotech.in/wp-content/plugins/portfolio-gallery/Front_images/projects/3.2.jpg;', 'http://huge-it.com/fields/order-website-maintenance/', 'image', 'on', 2, 1, NULL, 'My Third Category,'),
